import React, { Component } from 'react';
import {
  Row,
  Col,
  Table,
  Button,
  Badge,
  Breadcrumb,
  BreadcrumbItem,
  Alert,
} from 'reactstrap';
import { Link } from 'react-router-dom';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { getProducts } from '../../actions/products';
import { IMAGE_BASE_URL } from '../../constants/index';


class ProductList extends Component {
  static propTypes = {
    dispatch: PropTypes.func.isRequired,
    message: PropTypes.string,
    deleteMessage:PropTypes.string,
    statusMessage:PropTypes.string,
    isFetching: PropTypes.bool,
    products: PropTypes.array,

  };

  static defaultProps = {
    isFetching: false,
    message: null,
    deleteMessage:null,
    statusMessage:null,
    products:[],

  };
    constructor(props) {
        super(props);
        this.state = {
          products: [],
        };

        // this.handleClickDelete = this.handleClickDelete.bind(this);
        // this.handleClickStatus = this.handleClickStatus.bind(this);
      }

    componentDidMount = async () => {
      await this.props.getProducts();
      this.setState({ products : this.props.products });
    }

    handleClickDelete(id){
      try { 
        this.props
            .dispatch(
              // deleteCategory({
              //   categoryId: id,
              // }),
            )
            .then(() =>
              this.setState({

              }),
              this.doGetCategory()
            ); 
        }  catch (error) {
        console.log(error);
      } 
   };

   handleClickStatus(id){
    try { 
      this.props
          .dispatch(
            // statusCategory({
            //   categoryId: id,
            // }),
          )
          .then(() =>
            this.setState({

            }),
            this.doGetCategory()
          ); 
      }  catch (error) {
      console.log(error);
    } 
 };

    render() {

      var listData = this.state.products||[];
      console.log(listData.products);
       const CategoryData = listData.products !== undefined  && listData.products.length > 0 && listData.products.map((product,idx) => { 
        const self = this;   
            return  <>
            <tr key={product!== undefined && product.id}>
            <td scope="row" key={idx+1}>{idx+1}</td>
            <td>{product!== undefined && product.title}</td>
            <td>{product!== undefined && product.slug !==null ? product!== undefined && product.slug:'--'}</td>
            <td>{product!== undefined && product.image !==null ? <img className="img-rounded" src={IMAGE_BASE_URL+'/product_image/'+product.image} alt="" height="60" />:'--'}</td>
            <td>{product!== undefined && product.status === 1 ? <Badge color="gray" className="text-gray-light" key={idx+3} id={product.id} value={product.id} onClick={() => self.handleClickStatus(product.id)} pill>Active</Badge> : <Badge color="gray" className="text-gray-light" key={idx+4} id={product.id} value={product.id} onClick={() => self.handleClickStatus(product.id)} pill>Unactive</Badge> }</td>
            <td> 
                {/* <Link to={'/product/edit/'+product.id} className="btn btn-primary"> Edit </Link>    */}
                {/* <button className="btn btn-primary" onClick={() => self.handleClickEdit(product.id)}>Edit</button>/
                <button className="btn btn-danger" key={idx+2} id={product.id} value={product.id} onClick={() => self.handleClickDelete(employee.id)}>Delete</button> */}
                 <Link to={'categories/'+product.id}>
                 <Button outline color="warning" className="width-100 mb-xs mr-xs">Edit</Button>
                 </Link>
                 <Button outline color="danger" key={idx+2} id={product.id} value={product.id} className="width-100 mb-xs mr-xs" onClick={() => self.handleClickDelete(product.id)}>Delete</Button>
            </td> 
            </tr>
            </>;   
         });

        return (
        <div>
        <Breadcrumb>
        <BreadcrumbItem>YOU ARE HERE</BreadcrumbItem>
        <BreadcrumbItem active>Product Table</BreadcrumbItem>
        </Breadcrumb>
        <h1 className="page-title mb-lg">Product - <span className="fw-semi-bold">Basic</span></h1>

        <div className="">
        <Row>
          <Col lg={12}>
          {this.props.deleteMessage && (
                  <Alert className="alert-sm" bsstyle="info">
                    {this.props.deleteMessage}
                  </Alert>
                )}
          {this.props.statusMessage && (
                  <Alert className="alert-sm" bsstyle="info">
                    {this.props.statusMessage}
                  </Alert>
                )}
              <h3>Product <span className="fw-semi-bold">Table</span></h3>
              <Link to="/app/categories/new">
              <Button  outline color="primary" className="btn btn-sm btn-inverse" style={{float: "right"}}>Add</Button>
              </Link>
              <div className="table-responsive">
                <Table className="table-hover">
                  <thead>
                    <tr>
                      <th>#</th>
                      <th>Name</th>
                      <th>Slug</th>
                      <th>Image</th>
                      <th>Status</th>
                      <th>Action</th>
                    </tr>
                  </thead>
                  {/* eslint-disable */}
                  <tbody>
                    {CategoryData}
                  </tbody>
                  {/* eslint-enable */}
                </Table>
              </div>
          </Col>
        </Row>
       </div>
     </div>
       );
    }
}

function mapStateToProps(state) {
  return {
     message: state.products.message,
     products: state.products.products,
     //deleteMessage:state.products.deleteMessage,
    // statusMessage:state.products.statusMessage, 
  };
}
const actionCreators = { getProducts };
export default connect(mapStateToProps,actionCreators)(ProductList);
