import React from 'react';
import PropTypes from 'prop-types';
import { withRouter, Redirect,Link } from 'react-router-dom';
import { connect } from 'react-redux';
import { 
  Alert, 
  Button, 
  FormGroup, 
  Input, 
  Row,
  Col,
  Form
} from 'reactstrap';
import s from './Register.module.scss';
import Widget from '../../components/Widget';
import Footer from "../../components/Footer";
import { registerUser } from '../../actions/user';
import jwt from 'jsonwebtoken';
import config from '../../config'


class Register extends React.Component {

  static propTypes = {
    dispatch: PropTypes.func.isRequired,
    isAuthenticated: PropTypes.bool,
    isFetching: PropTypes.bool,
    location: PropTypes.any, // eslint-disable-line
    errorMessage: PropTypes.string,
  };

  static defaultProps = {
    isAuthenticated: false,
    isFetching: false,
    location: {},
    errorMessage: null,
  };

  static isAuthenticated(token) {
    // We check if app runs with backend mode
    if (!config.isBackend && token) return true;
    if (!token) return;
    const date = new Date().getTime() / 1000;
    const data = jwt.decode(token);
    console.log(data.exp);
    return date < data.exp;

}

  constructor(props) {
    super(props);

    this.state = {
      input: {
          email : "",
          name : "",
          password : "",
          confirmPassword: ""
      },  
      errors: {},
      successMessage: '',
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange = (event) => {
    let input = this.state.input;
    let errors = this.state.errors;

      input[event.target.name] = event.target.value;
      errors[event.target.name] = '';
      this.setState({
        input
      });
      console.log(this.state.input)

    }

    handleSubmit(event) {
      event.preventDefault();
    }   

  doLogin = (e) => {
    this.props.dispatch(
      registerUser({
        email: this.state.login,
        password: this.state.password,
      }),
    );
    e.preventDefault();
  }

  validate() {

    let input = this.state.input;
    let errors = {};
    let isValid = true;
    //console.log(input["profile_image"].size);
    if (!input["name"]) {
      isValid = false;
      errors["name"] = "Please enter your name.";
    }

    if (!input["employee_salary"]) {
      isValid = false;
      errors["employee_salary"] = "Please enter your employee salary.";
    }

    if (typeof input["employee_salary"] !== "undefined") {

      var pattern = new RegExp(/^[0-9\b]+$/);

      if (!pattern.test(input["employee_salary"])) {
        isValid = false;
        errors["employee_salary"] = "Please enter only number.";
      }
    }

    if (!input["employee_age"]) {
      isValid = false;
      errors["employee_age"] = "Please enter your employee age.";
    }

    if (typeof input["employee_age"] !== "undefined") {

      var pattern = new RegExp(/^[0-9\b]+$/);
      if (!pattern.test(input["employee_age"])) {
        isValid = false;
        errors["employee_age"] = "Please enter only number.";
      }

    }

    if (!input["email"]) {
      isValid = false;
      errors["email"] = "Please enter your email Address.";
    }

    if (typeof input["email"] !== "undefined") {

      var pattern = new RegExp(/^(("[\w-\s]+")|([\w-]+(?:\.[\w-]+)*)|("[\w-\s]+")([\w-]+(?:\.[\w-]+)*))(@((?:[\w-]+\.)*\w[\w-]{0,66})\.([a-z]{2,6}(?:\.[a-z]{2})?)$)|(@\[?((25[0-5]\.|2[0-4][0-9]\.|1[0-9]{2}\.|[0-9]{1,2}\.))((25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\.){2}(25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\]?$)/i);
      if (!pattern.test(input["email"])) {
        isValid = false;
        errors["email"] = "Please enter valid email address.";
      }
    }

    this.setState({
      errors: errors
    });

    return isValid;
  }
  render() {
    const {from} = this.props.location.state || {
      from: {pathname: '/login'},
    };

    if (this.props.isAuthenticated) {
      // cant access login page while logged in
      return <Redirect to={from} />;
    }

    return (
      <div className={s.root}>
      <Row>
        <Col xs={{size: 10, offset: 1}} sm={{size: 6, offset: 3}} lg={{size:4, offset: 4}}>
          <p className="text-center">React Dashboard</p>
          <Widget className={s.widget}>
            <h4 className="mt-0">Register to your Web App</h4>

            <Form className="mt" onSubmit={this.doRegister}>
              {this.props.errorMessage && (
                <Alert size="sm" color="danger">
                  {this.props.errorMessage}
                </Alert>
              )}
              <FormGroup className="form-group">
                <Input
                  className="no-border"
                  value={this.state.name}
                  onChange={this.handleChange}
                  type="text"
                  required
                  name="name"
                  placeholder="Name"
                />
                <div className="text-danger">{this.state.errors.name}</div>
              </FormGroup>
              <FormGroup className="form-group">
                <Input
                  className="no-border"
                  value={this.state.email}
                  onChange={this.handleChange}
                  type="email"
                  required
                  name="email"
                  placeholder="Email"
                />
                <div className="text-danger">{this.state.errors.email}</div>
              </FormGroup>
              <FormGroup>
                <Input
                  className="no-border"
                  value={this.state.password}
                  onChange={this.handleChange}
                  type="password"
                  required
                  name="password"
                  placeholder="Password"
                />
                <div className="text-danger">{this.state.errors.password}</div>
              </FormGroup>
              <FormGroup>
                <Input
                  className="no-border"
                  value={this.state.confirmPassword}
                  onChange={this.handleChange}
                  type="password"
                  required
                  name="confirmPassword"
                  placeholder="Confirm Password"
                />
              </FormGroup>
              <div className="d-flex justify-content-between align-items-center">
                {/* <a href="#" className="fs-sm">Trouble with account?</a> eslint-disable-line */}
                <Link to="/login">
                      <a className="fs-sm" color="default" size="sm">Login  
                      </a>
                  </Link>
                <div>

                  <Button color="success" size="sm" type="submit">
                    {this.props.isFetching ? 'Loading...' : 'Register'}
                  </Button>
                </div>
              </div>
            </Form>
          </Widget>
        </Col>
      </Row>
      <Footer className="text-center" />
      </div>
    );
  }
}

export default Register;
