import React, {PureComponent} from 'react';
import {
  Row,
  Col,
  Form,
  FormGroup,
  Label,
  Input,
  Button,
  ButtonGroup,
  Breadcrumb,
  BreadcrumbItem,
} from 'reactstrap';

import PropTypes from 'prop-types';
import { connect } from 'react-redux';

import Widget from '../../components/Widget/Widget';
import { createRole } from '../../../actions/roles';

import s from '../profile/Profile.module.scss';

class NewRole extends PureComponent {

  static propTypes = {
    dispatch: PropTypes.func.isRequired,
    message: PropTypes.string,
    isFetching: PropTypes.bool,
  };

  static defaultProps = {
    isFetching: false,
    message: null,
  };

  static meta = {
    name: 'Create new role',
  };

  constructor(props) {
    super(props);

    this.state = {
      name: '',
    };
  }

  changeName = (event) => {
    this.setState({name: event.target.value});
  }

  doCreatePost = (e) => {
    this.props
      .dispatch(
        createPost({
          title: this.state.title,
          content: this.state.content,
        }),
      )
      .then(() =>
        this.setState({
          title: '',
          content: '',
        }),
      );
    e.preventDefault();
  }

  onSubmit(e) { 
    e.preventDefault();
  }

  render() {
    return (
      <div className={s.root}>
        <Breadcrumb>
          <BreadcrumbItem>YOU ARE HERE</BreadcrumbItem>
          <BreadcrumbItem active>Role</BreadcrumbItem>
        </Breadcrumb>
        <h1 className="mb-lg">Role</h1>
        <Row>
          <Col sm={6}>
            <Widget
              title={
                <h5>
                  Add Role <span className="fw-semi-bold">Form</span>
                </h5>
              }
            >
              <Form onSubmit={this.onSubmit}>
              {this.props.message && (
                  <Alert className="alert-sm" bsstyle="info">
                    {this.props.message}
                  </Alert>
                )}
                <FormGroup>
                  <Label for="input-name">Name</Label>
                  <Input bsSize="lg" type="text" name="name" id="input-name"/>
                </FormGroup>

                <div className="d-flex justify-content-between align-items-center">
                  <ButtonGroup className="pull-right">
                    <Button className="ml-sm" color="default">Cancel</Button>
                    <Button color="danger">Save</Button>
                  </ButtonGroup>
                </div>
              </Form>
            </Widget>
          </Col>
        </Row>
      </div>
    )
  }
}
function mapStateToProps(state) {
    return {
      isFetching: state.roles.isFetching,
      message: state.roles.message,
    };
  }
export default connect(mapStateToProps)(NewRole);
