import React from 'react';
import PropTypes from 'prop-types';
import { withRouter, Redirect,Link } from 'react-router-dom';
import { connect } from 'react-redux';
import { 
  Alert, 
  Button, 
  FormGroup, 
  Input, 
  Row,
  Col,
  Form
} from 'reactstrap';
import s from './Login.module.scss';
import Widget from '../../components/Widget';
import Footer from "../../components/Footer";
import { loginUser } from '../../actions/user';
import jwt from 'jsonwebtoken';
import config from '../../config'


class Register extends React.Component {

  static propTypes = {
    dispatch: PropTypes.func.isRequired,
    isAuthenticated: PropTypes.bool,
    isFetching: PropTypes.bool,
    location: PropTypes.any, // eslint-disable-line
    errorMessage: PropTypes.string,
  };

  static defaultProps = {
    isAuthenticated: false,
    isFetching: false,
    location: {},
    errorMessage: null,
  };

  static isAuthenticated(token) {
    // We check if app runs with backend mode
    if (!config.isBackend && token) return true;
    if (!token) return;
    const date = new Date().getTime() / 1000;
    const data = jwt.decode(token);
    console.log(data.exp);
    return date < data.exp;

}

  constructor(props) {
    super(props);

    this.state = {
      login: '',
      password: '',
    };
  }

  changeLogin = (event) => {
    this.setState({login: event.target.value});
  }

  changePassword = (event) => {
    this.setState({password: event.target.value});
  }

  doLogin = (e) => {
    this.props.dispatch(
      loginUser({
        email: this.state.login,
        password: this.state.password,
      }),
    );
    e.preventDefault();
  }
  render() {
    const {from} = this.props.location.state || {
      from: {pathname: '/login'},
    };

    if (this.props.isAuthenticated) {
      // cant access login page while logged in
      return <Redirect to={from} />;
    }

    return (
      <div className={s.root}>
      <Row>
        <Col xs={{size: 10, offset: 1}} sm={{size: 6, offset: 3}} lg={{size:4, offset: 4}}>
          <p className="text-center">React Dashboard</p>
          <Widget className={s.widget}>
            <h4 className="mt-0">Login to your Web App</h4>
            <p className="fs-sm text-muted">
              User your username and password to sign in<br />
              Don&#39;t have an account? Sign up now!
            </p>
            <Form className="mt" onSubmit={this.doLogin}>
              {this.props.errorMessage && (
                <Alert size="sm" color="danger">
                  {this.props.errorMessage}
                </Alert>
              )}
              <FormGroup className="form-group">
                <Input
                  className="no-border"
                  value={this.state.login}
                  onChange={this.changeLogin}
                  type="text"
                  required
                  name="username"
                  placeholder="Username"
                />
              </FormGroup>
              <FormGroup>
                <Input
                  className="no-border"
                  value={this.state.password}
                  onChange={this.changePassword}
                  type="password"
                  required
                  name="password"
                  placeholder="Password"
                />
              </FormGroup>
              <div className="d-flex justify-content-between align-items-center">
                <a href="#" className="fs-sm">Trouble with account?</a> {/* eslint-disable-line */}
                <div>
                  <Link to="/register">
                      <Button color="default" size="sm">Create an account  
                      </Button>
                  </Link>
                  <Button color="success" size="sm" type="submit">
                    {this.props.isFetching ? 'Loading...' : 'Login'}
                  </Button>
                </div>
              </div>
            </Form>
          </Widget>
        </Col>
      </Row>
      <Footer className="text-center" />
      </div>
    );
  }
}

export default Register;
