import React, {PureComponent} from 'react';
import {
  Row,
  Col,
  Form,
  FormGroup,
  Label,
  Input,
  Button,
  ButtonGroup,
  Breadcrumb,
  BreadcrumbItem,
  Alert,
} from 'reactstrap';

import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import {Link } from 'react-router-dom';
import Widget from '../../components/Widget/Widget';
import { createCategory } from '../../actions/categories';

import s from '../profile/Profile.module.scss';

class CategoryNew extends PureComponent {

  static propTypes = {
    dispatch: PropTypes.func.isRequired,
    message: PropTypes.string,
    isFetching: PropTypes.bool,
  };

  static defaultProps = {
    isFetching: false,
    message: null,
  };

  constructor(props) {
    super(props);
    this.state = {
      input: {
          name:'',
          image:'',
          image_icon:'',
          parent_id:'',
      },
      formData: {},
      errors: {},
      successMessage: '',
    };
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(event) {
    let input = this.state.input;
    let errors = this.state.errors;
    if (event.target.name === "image") {
      console.log(event.target.files[0])
      input[event.target.name] = event.target.files[0];
      this.setState({
        input
      });
      console.log(this.state.input)
    } else if (event.target.name === "image_icon") {
        console.log(event.target.files[0])
        input[event.target.name] = event.target.files[0];
        this.setState({
          input
        });
        console.log(this.state.input)
      } else {
      //let input = this.state.input;
      //let errors = this.state.errors;
      input[event.target.name] = event.target.value;
      errors[event.target.name] = '';
      this.setState({
        input
      });
      console.log(this.state.input)

    }
  }

  changeName = (event) => {
    //console.log(event.target.value);
    this.setState({name: event.target.value});
  }

  doCreateRole = (e) => {
    this.props
      .dispatch(
        createCategory({
          name: this.state.name,
        }),
      )
      .then(() =>
        this.setState({
          name: '',
        }),
      );
    e.preventDefault();
  }

  onSubmit(e) { 
    e.preventDefault();
  }

  render() {
    return (
      <div className={s.root}>
        <Breadcrumb>
          <BreadcrumbItem>YOU ARE HERE</BreadcrumbItem>
          <BreadcrumbItem active>Role</BreadcrumbItem>
        </Breadcrumb>
        <h1 className="mb-lg">Role</h1>
        <Row>
          <Col sm={6}>
            <Widget
              title={
                <h5>
                  Add Role <span className="fw-semi-bold">Form</span>
                </h5>
              }
            >
              <Form onSubmit={this.doCreateRole}>
              {this.props.message && (
                  <Alert className="alert-sm" bsstyle="info">
                    {this.props.message}
                  </Alert>
                )}
                <FormGroup>
                  <Label for="input-name">Name</Label>
                  <Input bsSize="lg" type="text" name="name" id="input-name"  value={this.state.input.name} onChange={this.handleChange}/>
                  <div className="text-danger">{this.state.errors.name}</div>
                </FormGroup>

                <FormGroup>
                  <Label for="input-name">Image</Label>
                  <Input bsSize="lg" type="file" name="image" id="input-image"  value={this.state.input.image} onChange={this.handleChange}/>
                  <div className="text-danger">{this.state.errors.image}</div>
                </FormGroup>

                <div className="d-flex justify-content-between align-items-center">
                  <ButtonGroup className="pull-right">
                  <Link to="/app/roles">
                    <Button to="/app/roles" className="ml-sm" color="default">Cancel</Button>
                    </Link>
                    <Button color="danger" type="submit">
                      {this.props.isFetching ? 'Creating...' : 'Create'}
                    </Button>
                  </ButtonGroup>
                </div>
              </Form>
            </Widget>
          </Col>
        </Row>
      </div>
    )
  }
}
function mapStateToProps(state) {
    return {
       isFetching: state.roles.isFetching,
       message: state.roles.message,
    };
  }
export default connect(mapStateToProps)(CategoryNew);
