import {Type} from '../actions/type';  
  export default function products(
    state = {
      isFetching: false,
    },
    action,
  ) {
    switch (action.type) {  
      case Type.product.POST_PRODUCTS_REQUEST:
        return Object.assign({}, state, {
          isFetching: true,
        });
      case Type.product.GET_PRODUCTS_SUCCESS:
        return Object.assign({}, state, {
          isFetching: false,
          message: 'Category created successfully',
        });
      case Type.product.GET_PRODUCTS_FAILURE:
        return Object.assign({}, state, {
          isFetching: false,
          message:
            'Due to security reasons category creation is closed in demo version. Please setup locally to test',
        });
      default:
        return state;
    }
  }
