import React, {PureComponent} from 'react';
import {
  Row,
  Col,
  Form,
  FormGroup,
  Label,
  Input,
  Button,
  ButtonGroup,
  Breadcrumb,
  BreadcrumbItem,
  Alert,
} from 'reactstrap';

import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import {Link } from 'react-router-dom';
import Widget from '../../components/Widget/Widget';
import { updateCategory,createCategory, getCategoryName,fetchCategory } from '../../actions/categories';

import s from '../profile/Profile.module.scss';

class CategoryEdit extends PureComponent {

  static propTypes = {
    dispatch: PropTypes.func.isRequired,
    message: PropTypes.string,
    isFetching: PropTypes.bool,
    categoryName: PropTypes.object,
    categoryEditData:PropTypes.object,
  };

  static defaultProps = {
    isFetching: false,
    message: null,
    categoryName:{},
    categoryEditData:{},
  };

  constructor(props) {
    super(props);

    this.state = {
      name: '',
      input: {
        name:'',
        image:'',
        image_icon:'',
        parent_id:'',
      },
      formData: {},
      errors: {},
      categoryName: {},
      categoryEditData:{},
      id : this.props.match.params.id
    };
  }

  async componentDidMount(){
    var id = this.state.id;
     this.doEditCategory();
     this.doGetCategoryName();
  } 

  changeName = (event) => {
    //console.log(event.target.value);
    this.setState({name: event.target.value});
  }

  doEditCategory = () => {
    try { 
      this.props
          .dispatch(
            fetchCategory({
              categoryId: this.state.id,
            }),
          )
          .then(() =>
            this.setState({   
             categoryEditData: this.props.categoryEditData,
             //name: this.props.categoryEditData.name,
            }),
          ); 
          let input = {};
            input["name"] = this.state.categoryEditData.name;
            input["image"] = this.state.categoryEditData.image;
            input["image_icon"] = this.state.categoryEditData.image_icon;
            input["parent_id"] = this.state.categoryEditData.parent_id; 
            this.setState({input:input});
            console.log(this.props.categoryEditData); 
      }catch (error) {
      console.log(error);
    }  
  }

  doGetCategoryName = () => {
    try { 
      this.props
          .dispatch(
            getCategoryName({
              categoryName: this.state.categoryName,
            }),
          )
          .then(() =>
            this.setState({
              categoryName: this.props.categoryName,
            }),
          ); 
      }  catch (error) {
      console.log(error);
    }  
  }

  doUpdateCategory = (e) => {
    this.props
      .dispatch(
        updateCategory({
          name: this.state.name,
          id:this.state.id,
        }),
      )
      .then(() =>
        this.setState({
          name: '',
        }),
      );
    e.preventDefault();
  }

  onSubmit(e) { 
    e.preventDefault();
  }

  render() {
    var categoryNameData = this.state.categoryName.data||[];
    const CategoryData = categoryNameData.category_info != undefined  && categoryNameData.category_info.length > 0 && categoryNameData.category_info.map((category,idx) => { 
         return  <>
         <option key={idx+1} value={category.id}>{category.name}</option>
         </>;   
      });
    return (
      <div className={s.root}>
        <Breadcrumb>
          <BreadcrumbItem>YOU ARE HERE</BreadcrumbItem>
          <BreadcrumbItem active>Category</BreadcrumbItem>
        </Breadcrumb>
        <h1 className="mb-lg">Category</h1>
        <Row>
          <Col sm={6}>
            <Widget
              title={
                <h5>
                  Edit Category <span className="fw-semi-bold">Form</span>
                </h5>
              }
            >
              <Form onSubmit={this.doUpdateCategory}>
              {this.props.message && (
                  <Alert className="alert-sm" bsstyle="info">
                    {this.props.message}
                  </Alert>
                )}
                <FormGroup>
                  <Label for="input-name">Name</Label>
                  <Input bsSize="lg" type="text" name="name" id="input-name"  value={this.state.input.name}
                    required
                    onChange={this.changeName}/>
                </FormGroup>

                {CategoryData ?
                <FormGroup>
                    <Label for="exampleSelect">Select Parent Category</Label>
                    <Input type="select" name="parent_id" id="exampleSelect" onChange={this.handleChange}>
                    <option value="0">Select Category</option>  
                     {CategoryData}
                    </Input>
                    <div className="text-danger">{this.state.errors.parent_id}</div>
                </FormGroup>:""}

                <div className="d-flex justify-content-between align-items-center">
                  <ButtonGroup className="pull-right">
                  <Link to="/app/categories">
                    <Button to="/app/categories" className="ml-sm" color="default">Cancel</Button>
                    </Link>
                    <Button color="danger" type="submit">
                      {this.props.isFetching ? 'Updating...' : 'Update'}
                    </Button>
                  </ButtonGroup>
                </div>
              </Form>
            </Widget>
          </Col>
        </Row>
      </div>
    )
  }
}
function mapStateToProps(state) {
     console.log(state.categories.category);
    return {
       isFetching: state.categories.isFetching,
       message: state.categories.message,
       categoryName: state.categories.categoryName,
       categoryEditData:state.categories.categoryEditData,
    };
  }
export default connect(mapStateToProps)(CategoryEdit);
