import React, { Component } from 'react';
import {
  Row,
  Col,
  Table,
  Progress,
  Button,
  UncontrolledButtonDropdown,
  DropdownMenu,
  DropdownToggle,
  DropdownItem,
  Input,
  Label,
  Badge,
  Breadcrumb,
  BreadcrumbItem,
} from 'reactstrap';
import { Sparklines, SparklinesBars } from 'react-sparklines';

import Widget from '../../components/Widget';
import s from './Static.module.scss';

class Role extends Component {
    constructor(props) {
        super(props);

        this.state = {
          tableStyles: [
            {
              id: 1,
              picture: require('../../images/tables/1.jpg'), // eslint-disable-line global-require
              description: 'Palo Alto',
              info: {
                type: 'JPEG',
                dimensions: '200x150',
              },
              date: new Date('September 14, 2012'),
              size: '45.6 KB',
              progress: {
                percent: 29,
                colorClass: 'success',
              },
            },
            {
              id: 2,
              picture: require('../../images/tables/2.jpg'), // eslint-disable-line global-require
              description: 'The Sky',
              info: {
                type: 'PSD',
                dimensions: '2400x1455',
              },
              date: new Date('November 14, 2012'),
              size: '15.3 MB',
              progress: {
                percent: 33,
                colorClass: 'warning',
              },
            },
            {
              id: 3,
              picture: require('../../images/tables/3.jpg'), // eslint-disable-line global-require
              description: 'Down the road',
              label: {
                colorClass: 'success',
                text: 'INFO!',
              },
              info: {
                type: 'JPEG',
                dimensions: '200x150',
              },
              date: new Date('September 14, 2012'),
              size: '49.0 KB',
              progress: {
                percent: 38,
                colorClass: 'inverse',
              },
            },
            {
              id: 4,
              picture: require('../../images/tables/4.jpg'), // eslint-disable-line global-require
              description: 'The Edge',
              info: {
                type: 'PNG',
                dimensions: '210x160',
              },
              date: new Date('September 15, 2012'),
              size: '69.1 KB',
              progress: {
                percent: 17,
                colorClass: 'danger',
              },
            },
            {
              id: 5,
              picture: require('../../images/tables/5.jpg'), // eslint-disable-line global-require
              description: 'Fortress',
              info: {
                type: 'JPEG',
                dimensions: '1452x1320',
              },
              date: new Date('October 1, 2012'),
              size: '2.3 MB',
              progress: {
                percent: 41,
                colorClass: 'primary',
              },
            },
          ],
          checkboxes1: [false, true, false, false],
          checkboxes2: [false, false, false, false, false, false],
          checkboxes3: [false, false, false, false, false, false],
        };

        this.checkAll = this.checkAll.bind(this);
      }

      parseDate(date) {
        this.dateSet = date.toDateString().split(' ');
        return `${date.toLocaleString('en-us', { month: 'long' })} ${this.dateSet[2]}, ${this.dateSet[3]}`;
      }

      checkAll(ev, checkbox) {
        const checkboxArr = (new Array(this.state[checkbox].length)).fill(ev.target.checked);
        this.setState({
          [checkbox]: checkboxArr,
        });
      }

      changeCheck(ev, checkbox, id) {
        const { checkboxes1, checkboxes2, checkboxes3 } = this.state;
        if(checkbox === "checkboxes1") {
          const checkedBox1 = checkboxes1[id] = ev.target.checked;
          this.setState({
            checkedBox1
          }) 
        } else if (checkbox === "checkboxes2") {
          const checkedBox2 = checkboxes2[id] = ev.target.checked;
          this.setState({
            checkedBox2,
          })      
        } else {
          const checkedBox3 = checkboxes3[id] = ev.target.checked;
          this.setState({
            checkedBox3
          })  
        }


        this.setState({
          [checkbox]: this.state[checkbox],
        });
      }
    render() {
        return (
        <div>
        <Breadcrumb>
        <BreadcrumbItem>YOU ARE HERE</BreadcrumbItem>
        <BreadcrumbItem active>Role Table</BreadcrumbItem>
        </Breadcrumb>
        <h1 className="page-title mb-lg">Roles - <span className="fw-semi-bold">Basic</span></h1>

        <div className="row">
        <Row>
          <Col lg={12}>
              <h3>Hover <span className="fw-semi-bold">Table</span></h3>
              <p>{'Trace only what\'s really important. '}<code>.table-hover</code> is made for it.</p>
              <div className="table-responsive">
                <Table className="table-hover">
                  <thead>
                    <tr>
                      <th>#</th>
                      <th>First Name</th>
                      <th>Last Name</th>
                      <th>Email</th>
                      <th>Status</th>
                    </tr>
                  </thead>
                  {/* eslint-disable */}
                  <tbody>
                    <tr>
                      <td>1</td>
                      <td>Mark</td>
                      <td>Otto</td>
                      <td><a href="#">ottoto@example.com</a></td>
                      <td><Badge color="gray" className="text-gray-light" pill>Pending</Badge></td>
                    </tr>
                    <tr>
                      <td>2</td>
                      <td>Jacob</td>
                      <td>Thornton</td>
                      <td><a href="#">fat.thor@example.com</a></td>
                      <td><Badge color="gray" className="text-gray-light" pill>Unconfirmed</Badge></td>
                    </tr>
                    <tr>
                      <td>3</td>
                      <td>Larry</td>
                      <td>the Bird</td>
                      <td><a href="#">larry@example.com</a></td>
                      <td><Badge color="gray" className="text-gray-light" pill>New</Badge></td>
                    </tr>
                    <tr>
                      <td>4</td>
                      <td>Peter</td>
                      <td>Horadnia</td>
                      <td><a href="#">peter@example.com</a></td>
                      <td><Badge color="gray" className="text-gray-light" pill>Active</Badge></td>
                    </tr>
                  </tbody>
                  {/* eslint-enable */}
                </Table>
              </div>
          </Col>
        </Row>
       </div>
     </div>
       );
    }
}

export default Role;
