import React, { Component } from 'react';
import {
  Row,
  Col,
  Table,
  Progress,
  Button,
  UncontrolledButtonDropdown,
  DropdownMenu,
  DropdownToggle,
  DropdownItem,
  Input,
  Label,
  Badge,
  Breadcrumb,
  BreadcrumbItem,
} from 'reactstrap';
import { Link } from 'react-router-dom';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { getRoles } from '../../actions/roles';
import { Sparklines, SparklinesBars } from 'react-sparklines';

import Widget from '../../components/Widget/Widget';
import s from './Static.module.scss';

class Role extends Component {
  static propTypes = {
    dispatch: PropTypes.func.isRequired,
    message: PropTypes.string,
    isFetching: PropTypes.bool,
  };

  static defaultProps = {
    isFetching: false,
    message: null,
  };
    constructor(props) {
        super(props);

        this.state = {
          role: [],
        };

        this.checkAll = this.checkAll.bind(this);
      }



      parseDate(date) {
        this.dateSet = date.toDateString().split(' ');
        return `${date.toLocaleString('en-us', { month: 'long' })} ${this.dateSet[2]}, ${this.dateSet[3]}`;
      }

      checkAll(ev, checkbox) {
        const checkboxArr = (new Array(this.state[checkbox].length)).fill(ev.target.checked);
        this.setState({
          [checkbox]: checkboxArr,
        });
      }

      changeCheck(ev, checkbox, id) {
        const { checkboxes1, checkboxes2, checkboxes3 } = this.state;
        if(checkbox === "checkboxes1") {
          const checkedBox1 = checkboxes1[id] = ev.target.checked;
          this.setState({
            checkedBox1
          }) 
        } else if (checkbox === "checkboxes2") {
          const checkedBox2 = checkboxes2[id] = ev.target.checked;
          this.setState({
            checkedBox2,
          })      
        } else {
          const checkedBox3 = checkboxes3[id] = ev.target.checked;
          this.setState({
            checkedBox3
          })  
        }


        this.setState({
          [checkbox]: this.state[checkbox],
        });
      }
    render() {
        return (
        <div>
        <Breadcrumb>
        <BreadcrumbItem>YOU ARE HERE</BreadcrumbItem>
        <BreadcrumbItem active>Role Table</BreadcrumbItem>
        </Breadcrumb>
        <h1 className="page-title mb-lg">Roles - <span className="fw-semi-bold">Basic</span></h1>

        <div className="">
        <Row>
          <Col lg={12}>
              <h3>Role <span className="fw-semi-bold">Table</span></h3>
              <Link to="/app/roles/new">
              <Button  outline color="primary" className="btn btn-sm btn-inverse" style={{float: "right"}}>Add</Button>
              </Link>
              <div className="table-responsive">
                <Table className="table-hover">
                  <thead>
                    <tr>
                      <th>#</th>
                      <th>Name</th>
                      <th>Status</th>
                      <th>Action</th>
                    </tr>
                  </thead>
                  {/* eslint-disable */}
                  <tbody>
                    <tr>
                      <td>1</td>
                      <td>Mark</td>
                      <td><Badge color="gray" className="text-gray-light" pill>Pending</Badge></td>
                      <td>
                      <Button outline color="warning" className="width-100 mb-xs mr-xs">Edit</Button>
                      <Button outline color="danger" className="width-100 mb-xs mr-xs">Delete</Button>
                      </td>
                    </tr>
                    <tr>
                      <td>2</td>
                      <td>Jacob</td>                      
                      <td><Badge color="gray" className="text-gray-light" pill>Unconfirmed</Badge></td>
                      <td>
                      <Button outline color="warning" className="width-100 mb-xs mr-xs">Edit</Button>
                      <Button outline color="danger" className="width-100 mb-xs mr-xs">Delete</Button>
                      </td>
                    </tr>
                    <tr>
                      <td>3</td>
                      <td>Larry</td>                    
                      <td><Badge color="gray" className="text-gray-light" pill>New</Badge></td>
                      <td>
                      <Button outline color="warning" className="width-100 mb-xs mr-xs">Edit</Button>
                      <Button outline color="danger" className="width-100 mb-xs mr-xs">Delete</Button>
                      </td>
                    </tr>
                    <tr>
                      <td>4</td>
                      <td>Peter</td>                     
                      <td><Badge color="gray" className="text-gray-light" pill>Active</Badge></td>
                      <td>
                      <Button outline color="warning" className="width-100 mb-xs mr-xs">Edit</Button>
                      <Button outline color="danger" className="width-100 mb-xs mr-xs">Delete</Button>
                      </td>
                    </tr>
                  </tbody>
                  {/* eslint-enable */}
                </Table>
              </div>
          </Col>
        </Row>
       </div>
     </div>
       );
    }
}

export default Role;
