import {Type} from './type';
import { URL_ADD_PRODUCT,URL_PRODUCTS } from '../constants/index';
import { NotificationManager } from 'react-notifications';
import axios from 'axios';

export const POST_PRODUCTS_REQUEST   = 'POST_PRODUCTS_REQUEST';
export const GET_PRODUCTS_SUCCESS    = 'GET_PRODUCTS_SUCCESS';
export const GET_PRODUCTS_FAILURE    = 'GET_PRODUCTS_FAILURE';



// Get products actions.
export const getProducts = (userData, history) => async dispatch => {
    try {
        dispatch({ type: POST_PRODUCTS_REQUEST, payload: true });
        const response = await axios.get(URL_PRODUCTS, userData, {
            headers: { 'Content-Type': 'application/json' }
        });
        if (response.data.success === true) {
            dispatch({ type: GET_PRODUCTS_SUCCESS, payload: response.data.data });
        }
    } catch (error) {
        dispatch({ type: GET_PRODUCTS_FAILURE, payload: error });
    }
};

// Add product.
export const addProduct = (data) => async dispatch => {
    try {
        dispatch({ type: Type.product.POST_ADD_PRODUCT_REQUEST, payload: true });
        const response = await axios.post(URL_ADD_PRODUCT, data, {
            headers: { 
                'Content-Type': 'application/json',
                //'access-token': reactLocalStorage.get('access-token')
            }
        });
        if (response.data.success === true) {
            dispatch({ type: Type.product.GET_ADD_PRODUCT_SUCCESS, payload: response.data.data });
            NotificationManager.success(response.data.message, 'Product Added.');
        } else {
            NotificationManager.error(response.data.message, 'Something was wrong.');
        }
    } catch (error) {
        dispatch({ type: Type.product.GET_ADD_PRODUCT_FAILURE, payload: error });
    }
}
