import React, {PureComponent} from 'react';
import {
  Row,
  Col,
  Form,
  FormGroup,
  Label,
  Input,
  Button,
  ButtonGroup,
  Breadcrumb,
  BreadcrumbItem,
  Alert,
} from 'reactstrap';

import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import {Link } from 'react-router-dom';
import Widget from '../../components/Widget/Widget';
import { createCategory, getCategoryName } from '../../actions/categories';

import s from '../profile/Profile.module.scss';

class CategoryNew extends PureComponent {

  static propTypes = {
    dispatch: PropTypes.func.isRequired,
    message: PropTypes.string,
    isFetching: PropTypes.bool,
    categoryName:PropTypes.object,
  };

  static defaultProps = {
    isFetching: false,
    message: null,
    categoryName:{},
  };

  constructor(props) {
    super(props);
    this.state = {
      input: {
          name:'',
          image:'',
          image_icon:'',
          parent_id:'',
      },
      formData: {},
      errors: {},
      successMessage: '',
      categoryName:{}
    };
    this.handleChange = this.handleChange.bind(this);
  }

  async componentDidMount(){
    this.doGetCategoryName();
  }  

  doGetCategoryName = () => {
    try { 
      this.props
          .dispatch(
            getCategoryName({
              categoryName: this.state.categoryName,
            }),
          )
          .then(() =>
            this.setState({
              categoryName: this.props.categoryName,
            }),
          ); 
      }  catch (error) {
      console.log(error);
    }  
  }

  handleChange(event) {
    let input = this.state.input;
    let errors = this.state.errors;
    if (event.target.name === "image") {
      console.log(event.target.files[0])
      input[event.target.name] = event.target.files[0];
      this.setState({
        input
      });
      //console.log(this.state.input)
    } else if (event.target.name === "image_icon") {
        console.log(event.target.files[0])
        input[event.target.name] = event.target.files[0];
        this.setState({
          input
        });
        //console.log(this.state.input)
      } else {
      console.log(event.target.name);
      input[event.target.name] = event.target.value;
      errors[event.target.name] = '';
      this.setState({
        input
      });
      console.log(this.state.input)

    }
  }

  changeName = (event) => {
    //console.log(event.target.value);
    this.setState({name: event.target.value});
  }

  doCreateCategory = (e) => {
    this.props
      .dispatch(
        createCategory({
          name: this.state.input.name,
        }),
      )
      .then(() =>
        this.setState({
          name: '',
        }),
      );
    e.preventDefault();
  }

  validate() {

    let input = this.state.input;
    let errors = {};
    let isValid = true;
    //console.log(input["profile_image"].size);
    if (!input["name"]) {
      isValid = false;
      errors["name"] = "Please enter your name.";
    }

    if (!input["employee_salary"]) {
      isValid = false;
      errors["employee_salary"] = "Please enter your employee salary.";
    }

    if (typeof input["employee_salary"] !== "undefined") {

      var pattern = new RegExp(/^[0-9\b]+$/);

      if (!pattern.test(input["employee_salary"])) {
        isValid = false;
        errors["employee_salary"] = "Please enter only number.";
      }
    }

    if (!input["employee_age"]) {
      isValid = false;
      errors["employee_age"] = "Please enter your employee age.";
    }

    if (typeof input["employee_age"] !== "undefined") {

      var pattern = new RegExp(/^[0-9\b]+$/);
      if (!pattern.test(input["employee_age"])) {
        isValid = false;
        errors["employee_age"] = "Please enter only number.";
      }

    }

    if (!input["email"]) {
      isValid = false;
      errors["email"] = "Please enter your email Address.";
    }

    if (typeof input["email"] !== "undefined") {

      var pattern = new RegExp(/^(("[\w-\s]+")|([\w-]+(?:\.[\w-]+)*)|("[\w-\s]+")([\w-]+(?:\.[\w-]+)*))(@((?:[\w-]+\.)*\w[\w-]{0,66})\.([a-z]{2,6}(?:\.[a-z]{2})?)$)|(@\[?((25[0-5]\.|2[0-4][0-9]\.|1[0-9]{2}\.|[0-9]{1,2}\.))((25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\.){2}(25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\]?$)/i);
      if (!pattern.test(input["email"])) {
        isValid = false;
        errors["email"] = "Please enter valid email address.";
      }
    }

    if (!input["mobile_number"]) {
      isValid = false;
      errors["mobile_number"] = "Please enter your phone number.";
    }

    if (typeof input["mobile_number"] !== "undefined") {

      var pattern = new RegExp(/^[0-9\b]+$/);
      //var pattern = new RegExp(/^\+(?:[0-9] ?){6,14}[0-9]$/);
      if (!pattern.test(input["mobile_number"])) {
        isValid = false;
        errors["mobile_number"] = "Please enter only number.";
      } else if (input["mobile_number"].length < 7) {
        isValid = false;
        errors["mobile_number"] = "Please enter valid phone number.";
      }
    }

    if (!input["address"]) {
      isValid = false;
      errors["address"] = "Please enter your address.";
    }

    if (!input["date"]) {
      isValid = false;
      errors["date"] = "Please select date.";
    }

    if (!input["gender"]) {
      isValid = false;
      errors["gender"] = "Please select gender.";
    }

    if (!this.state.checked) {
      if (!input["checkbox"]) {
        isValid = false;
        errors["checkbox"] = "Please Select Terms & Conditions.";
      }
    }

    if (!input["profile_image"]) {
      isValid = false;
      errors["profile_image"] = "Please select image.";
    }

    if (typeof input["profile_image"] !== "undefined" && input["profile_image"]===null && input["profile_image"]===undefined) {
       debugger
      let imageSize = input["profile_image"].size;
      if (!input["profile_image"].name.match(/\.(jpg|jpeg|png|gif)$/)) {
        isValid = false;
        errors["profile_image"] = "Please select valid image(like jpg|jpeg|png|gif).";
      } else if (imageSize > 1048576) {
        isValid = false;

        errors["profile_image"] = "Image size less than 1 MB.";
      }
    }

    this.setState({
      errors: errors
    });

    return isValid;
  }

  onSubmit(e) { 
    e.preventDefault();
  }

  render() {
    var categoryNameData = this.state.categoryName.data||[];
     const CategoryData = categoryNameData.category_info != undefined  && categoryNameData.category_info.length > 0 && categoryNameData.category_info.map((category,idx) => { 
          return  <>
          <option key={idx+1} value={category.id}>{category.name}</option>
          </>;   
       });
    return (
      <div className={s.root}>
        <Breadcrumb>
          <BreadcrumbItem>YOU ARE HERE</BreadcrumbItem>
          <BreadcrumbItem active>Category</BreadcrumbItem>
        </Breadcrumb>
        <h1 className="mb-lg">Category</h1>
        <Row>
          <Col sm={6}>
            <Widget
              title={
                <h5>
                  Add Category <span className="fw-semi-bold">Form</span>
                </h5>
              }
            >
              <Form onSubmit={this.doCreateCategory}>
              {this.props.message && (
                  <Alert className="alert-sm" bsstyle="info">
                    {this.props.message}
                  </Alert>
                )}
                <FormGroup>
                  <Label for="input-name">Name</Label>
                  <Input bsSize="lg" type="text" name="name" id="input-name"  value={this.state.input.name} onChange={this.handleChange}/>
                  <div className="text-danger">{this.state.errors.name}</div>
                </FormGroup>

                <FormGroup>
                  <Label for="input-name">Image</Label>
                  <Input bsSize="lg" type="file" name="image" id="input-image"  value={this.state.input.image} onChange={this.handleChange}/>
                  <div className="text-danger">{this.state.errors.image}</div>
                </FormGroup>

                <FormGroup>
                  <Label for="input-name">Image Icon</Label>
                  <Input bsSize="lg" type="file" name="image_icon" id="input-image_icon"  value={this.state.input.image_icon} onChange={this.handleChange}/>
                  <div className="text-danger">{this.state.errors.image_icon}</div>
                </FormGroup>
                {CategoryData ?
                <FormGroup>
                    <Label for="exampleSelect">Select Parent Category</Label>
                    <Input type="select" name="parent_id" id="exampleSelect" onChange={this.handleChange}>
                    <option >Select Category</option>  
                     {CategoryData}
                    </Input>
                    <div className="text-danger">{this.state.errors.parent_id}</div>
                </FormGroup>:""}

                <div className="d-flex justify-content-between align-items-center">
                  <ButtonGroup className="pull-right">
                  <Link to="/app/categories">
                    <Button to="/app/categories" className="ml-sm" color="default">Cancel</Button>
                    </Link>
                    <Button color="danger" type="submit">
                      {this.props.isFetching ? 'Creating...' : 'Create'}
                    </Button>
                  </ButtonGroup>
                </div>
              </Form>
            </Widget>
          </Col>
        </Row>
      </div>
    )
  }
}
