import React, { Component } from 'react';
import {
  Row,
  Col,
  Table,
  Progress,
  Button,
  UncontrolledButtonDropdown,
  DropdownMenu,
  DropdownToggle,
  DropdownItem,
  Input,
  Label,
  Badge,
  Breadcrumb,
  BreadcrumbItem,
} from 'reactstrap';
import { Link } from 'react-router-dom';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { getRoles } from '../../actions/roles';


class RoleList extends Component {
  static propTypes = {
    dispatch: PropTypes.func.isRequired,
    message: PropTypes.string,
    isFetching: PropTypes.bool,
    role: PropTypes.array,
  };

  static defaultProps = {
    isFetching: false,
    message: null,
    roles:[],
  };
    constructor(props) {
        super(props);

        this.state = {
          roles: [],
        };

        this.doGetRole = this.doGetRole.bind(this);
      }

    componentDidMount(){
      this.doGetRole();
      console.log(this.props.roles);
    }  

      doGetRole = () => {
        this.props
          .dispatch(
            getRoles({
              roles: this.state.roles,
            }),
          )
          .then(() =>
            this.setState({
              roles: this.props.roles,
            }),
          );  
      }

    render() {
        return (
        <div>
        <Breadcrumb>
        <BreadcrumbItem>YOU ARE HERE</BreadcrumbItem>
        <BreadcrumbItem active>Role Table</BreadcrumbItem>
        </Breadcrumb>
        <h1 className="page-title mb-lg">Roles - <span className="fw-semi-bold">Basic</span></h1>

        <div className="">
        <Row>
          <Col lg={12}>
              <h3>Role <span className="fw-semi-bold">Table</span></h3>
              <Link to="/app/roles/new">
              <Button  outline color="primary" className="btn btn-sm btn-inverse" style={{float: "right"}}>Add</Button>
              </Link>
              <div className="table-responsive">
                <Table className="table-hover">
                  <thead>
                    <tr>
                      <th>#</th>
                      <th>Name</th>
                      <th>Status</th>
                      <th>Action</th>
                    </tr>
                  </thead>
                  {/* eslint-disable */}
                  <tbody>
                    <tr>
                      <td>1</td>
                      <td>Mark</td>
                      <td><Badge color="gray" className="text-gray-light" pill>Pending</Badge></td>
                      <td>
                      <Button outline color="warning" className="width-100 mb-xs mr-xs">Edit</Button>
                      <Button outline color="danger" className="width-100 mb-xs mr-xs">Delete</Button>
                      </td>
                    </tr>
                    <tr>
                      <td>2</td>
                      <td>Jacob</td>                      
                      <td><Badge color="gray" className="text-gray-light" pill>Unconfirmed</Badge></td>
                      <td>
                      <Button outline color="warning" className="width-100 mb-xs mr-xs">Edit</Button>
                      <Button outline color="danger" className="width-100 mb-xs mr-xs">Delete</Button>
                      </td>
                    </tr>
                    <tr>
                      <td>3</td>
                      <td>Larry</td>                    
                      <td><Badge color="gray" className="text-gray-light" pill>New</Badge></td>
                      <td>
                      <Button outline color="warning" className="width-100 mb-xs mr-xs">Edit</Button>
                      <Button outline color="danger" className="width-100 mb-xs mr-xs">Delete</Button>
                      </td>
                    </tr>
                    <tr>
                      <td>4</td>
                      <td>Peter</td>                     
                      <td><Badge color="gray" className="text-gray-light" pill>Active</Badge></td>
                      <td>
                      <Button outline color="warning" className="width-100 mb-xs mr-xs">Edit</Button>
                      <Button outline color="danger" className="width-100 mb-xs mr-xs">Delete</Button>
                      </td>
                    </tr>
                  </tbody>
                  {/* eslint-enable */}
                </Table>
              </div>
          </Col>
        </Row>
       </div>
     </div>
       );
    }
}

function mapStateToProps(state) {
  return {
     isFetching: state.roles.isFetching,
     message: state.roles.message,
     roles: state.roles.roles,
  };
}

export default connect(mapStateToProps)(RoleList);
